from typing import Optional, Dictfrom enum import Enumclass ProgLang(Enum):    JAVA, JAVASCRIPT, PYTHON, GO = range(4)    @staticmethod    def str_to_lang(lang: str) -> Optional['ProgLang']:        return _language_string_to_language_map.get(lang, None)def _language_string_to_language_map() -> Dict[str, ProgLang]:    """ 语言名字符串与语言之间的匹配. """    return {e.name.lower(): e for e in ProgLang}def str_to_lang(lang: str) -> Optional[ProgLang]:    return _language_string_to_language_map.get(lang, None)